// src/components/Chat.js
import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

// Verbind met je backend. Pas het adres en de poort aan indien nodig.
const socket = io('http://localhost:5000');

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  
  // Stel een roomId en senderId in; voor een test kun je vaste waarden gebruiken.
  const roomId = 'room1';
  const senderId = 1; // Voor testdoeleinden

  useEffect(() => {
    // Laat de client de room joinen
    socket.emit('joinRoom', roomId);
    
    // Luister naar inkomende berichten
    socket.on('chat message', (data) => {
      setMessages((prev) => [...prev, data]);
    });

    // Clean-up wanneer de component unmount
    return () => {
      socket.off('chat message');
    };
  }, [roomId]);

  const sendMessage = () => {
    if (input.trim() !== '') {
      socket.emit('chat message', { roomId, senderId, message: input });
      setInput('');
    }
  };

  return (
    <div style={{ margin: '20px' }}>
      <h2>Chat Room: {roomId}</h2>
      <div style={{ border: '1px solid #ccc', padding: '1rem', height: '300px', overflowY: 'scroll' }}>
        {messages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.senderId === senderId ? 'Ik' : 'Partner'}:</strong> {msg.message}
          </div>
        ))}
      </div>
      <div style={{ marginTop: '10px' }}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Typ je bericht..."
          style={{ width: '70%', padding: '8px' }}
        />
        <button onClick={sendMessage} style={{ padding: '8px 16px', marginLeft: '10px' }}>
          Verstuur
        </button>
      </div>
    </div>
  );
};

export default Chat;
